import { FileModelSAFT } from "../validations/model/saft/FileModelSAFT";
import { GeneralLedgerEntriesMapToModel } from "./saft/general_ledger/general_ledger_entries_map_to_model";
import { HeaderToModel } from "./saft/header_to_model";
import { MasterFilesMapToModel } from "./saft/masterfiles_map_to_model";
import { SourceDocumentsToModel } from "./saft/source_documents/source_documents_model";

export class MapToModel {


    static getSaftModel({ generalLedgerEntries, headerMap, masterFilesMap, sourceDocumentsMap }:
        { generalLedgerEntries: Map<string, Object>, headerMap: Map<string, Object>, masterFilesMap: Map<string, Object>, sourceDocumentsMap: Map<string, Object> }): FileModelSAFT {

        let saftModel = new FileModelSAFT()


        saftModel.header = new HeaderToModel({ headerMap }).get()
        saftModel.masterFiles = new MasterFilesMapToModel({ masterFilesMap }).get()
        saftModel.sourceDocuments = new SourceDocumentsToModel(sourceDocumentsMap).get()
        saftModel.generalLedgerEntries = new GeneralLedgerEntriesMapToModel(generalLedgerEntries).get()

        return saftModel

    }

}